import React, { useState } from "react";


function Item(props) {
    const [lineThrough, setLineThrough] = useState

    function clicked(){
        textDecoration = line-through
    }

    return (
        <li onClick = {clicked}> {props.text} </li>
    )
}
// when you click it trigger the useState
////onclick = attribute 



export default Item;
