import React, { useState } from "react";


function Item(props) {
    const [isClicked, setIsClicked] = useState(false);
    let textDeco = {};

    function click(){
        if (isClicked === false) {
            setIsClicked(true)
        }
        else {
            setIsClicked(false)
        }
    }

    return (
        <li onClick = {click} style = {isClicked ? {textDecoration: "line-through"} : {}}> {props.text} </li>
    )
}
// when you click it trigger the useState
////onclick = attribute 



export default Item;
