import React, { useState } from "react";

function App() {
  const [addedItem, setAddedItem] = useState("");
  const [listArray, setListArray] = useState([]);
  const [toDoList, setToDoList] = useState("");

  function inputChanged(event) {
    setAddedItem(event.target.value)
  }

  function submitItem() {
    console.log(addedItem);
    setListArray(prevArray => [...prevArray, addedItem]);
    console.log(listArray)
    // let newToDoList = listArray.forEach(item => ("<li>" + item + "</li>"))
    // console.log(newToDoList)
    let newToDoList = ""
    console.log(newToDoList);
    newToDoList += listArray.forEach(item => ("<li>" + item + "</li>"))
    setToDoList(newToDoList);
    }


  return (
    <div className="container">
      <div className="heading">
        <h1>To-Do List</h1>
      </div>
      <div className="form">
        <input onChange={inputChanged} type="text" value = {addedItem}/>
        <button onClick={submitItem} >
          <span>Add</span>
        </button>
      </div>
      <div>
        <ul>
          {toDoList}
        </ul>
      </div>
    </div>
  );
}

//CHALLENGE: Make this app work by applying what you've learnt.
//1. When new text is written into the input, its state should be saved.
//2. When the add button is pressed, the current data in the input should be
//added to an array.
//3. The <ul> should display all the array items as <li>s

export default App;
