import React, { useState } from "react";


function Item(props) {
    // const [lineThrough, setLineThrough] = useState[false];
    let textDeco = {};

    function clicked(){
        if (lineThrough === false) {
            textDeco = {textDecoration: "line-through"}
            setLineThrough(true)
        }
        else {
            textDeco = {}
            setLineThrough(false)
        }
    }


    return (
        <li onClick = {clicked} style = {textDeco}> {props.text} </li>
    )
}
// when you click it trigger the useState
////onclick = attribute 



export default Item;
