import React, { useState } from "react";

function App() {

  const [Heading, setHeading] = useState("Hello");

  function submit() {
    setHeading("Submitted")
  };

  return (
    <div className="container">
      <h1>{Heading}</h1>
      <input type="text" placeholder="What's your name?" />
      <button onClick = {submit}>Submit</button>
    </div>
  );
}

export default App;
